import React, { Component } from 'react';
import './Map.css';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import userLocationURL from './user_location.svg';

class MapComponent extends Component {
  state = {
    location: {
        lat: 0,
         lng: 0
    },
    zoom: 13,
    locationName: "Mi Lugar Favorito",
  }

  componentDidMount() {
    navigator.geolocation.getCurrentPosition(getUserLocation, failedGettingLocation, {enableHighAccuracy: true});
  }

  render(){    
    const position = [this.state.location.lat, this.state.location.lng]
    const myIcon = () => {
      L.icon({
        iconUrl: userLocationURL,
        iconSize: [50, 82],
        popupAnchor: [0, -15]
      });
    };

    const getUserLocation = (position) => {
      this.setState(
          {
              location: {
                lat: position.coords.latitude,
                lng: position.coords.longitude
              }
          });
      }

    const failedGettingLocation = () => {
    fetch('https://ipapi.co/json').then(response => response.json())
    .then(location => {
      this.setState({
        location: {
          lat: location.latitude,
          lng: location.longitude
        },
      });
    })
  }  
        return (
          <Map className="Map" center={position} zoom={this.state.zoom}>
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
            <Marker 
              position={position}
              icon={myIcon}>
              <Popup>
                {this.state.locationName}
              </Popup>
            </Marker>
          </Map>
        )
    }
}

export default MapComponent
