import React, { Component } from 'react';
import './Map.css';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import userLocationURL from './user_location.svg';

const myIcon = L.icon({
  iconUrl: userLocationURL,
  iconSize: [50, 82],
  popupAnchor: [0, -15]
});

class MapComponent extends Component {
  state = {
    userLocation: {
      lat: 3.4372201,
      lng: -76.5224991
    },
    markers: [[0,0]],
    zoom: 13,
    draggable: true,
  }

  getUserLocation = (position) => {
    this.setState(
      {
        location: {
          lat: position.coords.latitude,
          lng: position.coords.longitude
        },
      });
  }

  failedGettingLocation = () => {
    fetch('https://ipapi.co/json').then(response => response.json())
    .then(location => {
      this.setState({
        location: {
          lat: location.latitude,
          lng: location.longitude
        },
      });
    })
  }

  addMarker = (e) => {
    const {markers} = this.state
    markers.push(e.latlng)
    this.setState({markers})
  }


  componentDidMount() {
    navigator.geolocation.getCurrentPosition(this.getUserLocation, this.failedGettingLocation, {enableHighAccuracy: true});
  }


  render(){    
    const position = [this.state.userLocation.lat, this.state.userLocation.lng]
    return (
      <Map className="Map" center={position} zoom={this.state.zoom}
        onClick={this.addMarker}>
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
          {this.state.markers.map((position, idx) => 
          <Marker key={`marker-${idx}`} position={position} icon={myIcon}>
          <Popup>
            <span>A pretty CSS3 popup. <br/> Easily customizable.</span>
          </Popup>
        </Marker>
        )}
      </Map>
    )
  }
}

export default MapComponent
