import React, { Component } from 'react';
import './Map.css';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import userLocationURL from './user_location.svg';

const myIcon = L.icon({
  iconUrl: userLocationURL,
  iconSize: [50, 82],
  popupAnchor: [0, -15]
});

type Position = { lat: number, lng: number }

type State = {
  center: Position,
  marker: Position,
  zoom: number,
  locationName: string,
  draggable: boolean,
}

class MapComponent extends Component {
  state = {
    center: {
      lat: 3.4372201,
      lng: -76.5224991
    },
    marker: {
      lat: 3.4372201,
      lng: -76.5224991
    },
    zoom: 13,
    locationName: "Mi Lugar Favorito",
    draggable: true,
  }

  getUserLocation = (position) => {
    this.setState(
      {
        location: {
          lat: position.coords.latitude,
          lng: position.coords.longitude
        },
      });
  }

  failedGettingLocation = () => {
    fetch('https://ipapi.co/json').then(response => response.json())
    .then(location => {
      this.setState({
        location: {
          lat: location.latitude,
          lng: location.longitude
        },
      });
    })
  } 

  componentDidMount() {
    navigator.geolocation.getCurrentPosition(this.getUserLocation, this.failedGettingLocation, {enableHighAccuracy: true});
  }

  toggleDraggable = () => {
    this.setState({ draggable: !this.state.draggable })
  }

  updatePosition = () => {
    const marker = this.refmarker.current
    if (marker != null) {
      this.setState({
        marker: marker.leafletElement.getLatLng(),
      })
    }
  }

  render(){    
    const position = [this.state.location.lat, this.state.location.lng]
    return (
      <Map className="Map" center={position} zoom={this.state.zoom}>
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"/>
        <Marker 
          icon={myIcon}
          draggable={this.state.draggable}
          onDragend={this.updatePosition}
          position={position}>
          <Popup>
            {this.state.locationName}
            <span onClick={this.toggleDraggable}>
              {this.state.draggable ? 'DRAG MARKER' : 'MARKER FIXED'}
            </span>
          </Popup>
        </Marker>
      </Map>
    )
  }
}

export default MapComponent
